import React, { useState } from 'react';

// CustomDropdown component
const CustomDropdown = ({ options, selectedValue, onChange }) => {
  const [isOpen, setIsOpen] = useState(false);

  const handleOptionClick = (value) => {
    onChange(value);
    setIsOpen(false);
  };

  return (
    <div className="relative inline-block text-left">
      <div>
        <button
          type="button"
          className="inline-flex w-full justify-between p-2 border border-gray-300 rounded-md bg-white text-gray-700 hover:text-gray-900 focus:outline-none focus:ring-1 focus:ring-blue-500"
          onClick={() => setIsOpen(!isOpen)}
        >
          {selectedValue ? options.find(option => option.value === selectedValue)?.label : 'Select an option'}
          <svg
            className="-mr-1 ml-2 h-5 w-5"
            xmlns="http://www.w3.org/2000/svg"
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
          >
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
          </svg>
        </button>
      </div>

      {isOpen && (
        <div className="absolute mt-2 w-full bg-white border border-gray-300 rounded-md shadow-lg z-10">
          {options.map(option => (
            <button
              key={option.value}
              type="button"
              className="block w-full text-left px-4 py-2 text-gray-700 hover:bg-gray-100"
              onClick={() => handleOptionClick(option.value)}
            >
              {option.label}
            </button>
          ))}
        </div>
      )}
    </div>
  );
};

// Usage of CustomDropdown in your Form component
const Form = ({ isOpen, setIsOpen }) => {
  const initialSchemas = [
    { label: 'First Name', value: 'first_name' },
    { label: 'Last Name', value: 'last_name' },
    { label: 'Gender', value: 'gender' },
    { label: 'Age', value: 'age' },
    { label: 'Account Name', value: 'account_name' },
    { label: 'City', value: 'city' },
    { label: 'State', value: 'state' }
  ];

  const [segmentName, setSegmentName] = useState('');
  const [selectedSchemas, setSelectedSchemas] = useState([]);
  const [availableSchemas, setAvailableSchemas] = useState(initialSchemas);
  const [selectedSchema, setSelectedSchema] = useState('');

  const handleAddSchema = () => {
    if (selectedSchema) {
      const schema = availableSchemas.find(s => s.value === selectedSchema);
      if (schema && !selectedSchemas.some(s => s.value === selectedSchema)) {
        setSelectedSchemas(prevSchemas => [...prevSchemas, schema]);
        setAvailableSchemas(prevSchemas => prevSchemas.filter(s => s.value !== selectedSchema));
        setSelectedSchema('');
      }
    }
  };

  const handleSchemaChange = (index, value) => {
    const newSchema = initialSchemas.find(s => s.value === value);
    if (newSchema) {
      setSelectedSchemas(prevSchemas => {
        const updatedSchemas = prevSchemas.map((s, i) =>
          i === index ? newSchema : s
        );
        return updatedSchemas;
      });
    }
  };

  const getOptions = (currentValue) => {
    return availableSchemas.filter(s => s.value !== currentValue);
  };

  const handleSaveSegment = () => {
    const data = {
      segment_name: segmentName,
      schema: selectedSchemas.map(s => ({ [s.value]: s.label }))
    };
    console.log('Saving data to server:', data);
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 flex items-center justify-end bg-gray-500 bg-opacity-75">
      <div className="relative bg-white w-1/3 h-full p-6 shadow-lg">
        <button
          className="absolute top-2 right-2 text-gray-600 hover:text-gray-800"
          onClick={() => setIsOpen(false)}
        >
          &times;
        </button>
        <div className="header flex items-center">
          <span className="w-[24px] block cursor-pointer" onClick={() => setIsOpen(false)}>
            <img src="../arrow-drop-left-line.png" alt="Close" />
          </span>
          <h2 className="text-xl font-bold">Saving Segment</h2>
        </div>
        <div className="mt-4">
          <p>Enter the name of the segment</p>
          <input
            type="text"
            placeholder="Enter segment name"
            value={segmentName}
            onChange={(e) => setSegmentName(e.target.value)}
            className="p-2 border border-gray-300 rounded-md focus:outline-none focus:border-blue-500 focus:ring-1 focus:ring-blue-500 w-full"
          />
          <p className="mt-4">To save your segment, you need to build your query.</p>
          <div className="mt-4">
            <CustomDropdown
              options={availableSchemas}
              selectedValue={selectedSchema}
              onChange={setSelectedSchema}
            />
            <button
              onClick={handleAddSchema}
              className="mt-2 px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600"
            >
              + Add new schema
            </button>
          </div>
          <div className="mt-4 bg-blue-100 p-4 rounded-md">
            <p>schemas-{JSON.stringify(selectedSchemas)}</p>
            {selectedSchemas.map((schema, index) => (
              <div key={index} className="mb-2">
                <CustomDropdown
                  options={getOptions(schema.value)}
                  selectedValue={schema.value}
                  onChange={(value) => handleSchemaChange(index, value)}
                />
                <p className="mt-2">Selected value: {schema.label}</p>
              </div>
            ))}
          </div>
          <button
            onClick={handleSaveSegment}
            className="mt-4 px-4 py-2 bg-green-500 text-white rounded-md hover:bg-green-600"
          >
            Save Segment
          </button>
        </div>
      </div>
    </div>
  );
};

export default Form;
